import './UserInputComponent.css';
import { SelectTipComponent } from './SelectTipComponent';
import { UserInputBox } from '../UI/UserInputBox';
import dollar from '../../assets/icon-dollar.svg'
import person from '../../assets/icon-person.svg'
import { useState } from 'react';
import { ContextProvider } from '../../store/BillContext';
import { useContext } from 'react';


export const UserInputComponent =()=>{
    console.log("UserInputComponent is executed")
    const billContext = useBillContext()

    const [currentBillAmount, setCurrentBillAmount] = useState("0");


    const setBillAmount =(event)=>{
        console.log("value is changed")
       // setCurrentBillAmount(event.target.value);
       billContext.setBillAmount(event.target.value)
    }

    return(    

       <div id='container-input'>        
        <div>
            <label>Bill</label><br></br>
            <UserInputBox img={dollar} cuurValue={currentBillAmount} onText={(event)=>setBillAmount(event)} ></UserInputBox>
        </div>
        <div>
        <label>Select Tip</label>
        <SelectTipComponent></SelectTipComponent>
        </div>  
        <div>
        <label>No of People</label><br></br>
        <UserInputBox img={person} cuurValue={currentBillAmount} onText={(event)=>setBillAmount(event)}></UserInputBox>
        </div>

        </div>         

    )
}
