import './UserInputBox.css'
import { useSelector,useDispatch } from 'react-redux'


export const CustomTip =()=>{
    const dispatch  = useDispatch()
    const customTip = useSelector(state=>state.customTip)


    const customTipHandler =(percentValue)=>{
        const percent = Number(percentValue)


        dispatch({type:'set-custom-tip',percentamount:percent});

    }

    return(
        <div className="container-custom">      
        <input className='container-child' onChange={(event)=>customTipHandler(event)} required value= {customTip} type="number"></input>
        </div>
    )
}
