import { createStore } from "redux";
const billReducer = ( state = {TipPercent : 0},action)=>{
     if(action.type==='set-tip-percenttage'){
        return{
            TipPercent:state.TipPercent+5,
        }
     }

     return state
}

export const store = createStore(billReducer);
