import './UserInputBox.css'
import { useSelector,useDispatch } from 'react-redux'


export const CustomTip =()=>{
    const billContext = useBillContext()


    const customTipHandler =(event)=>{
        console.log("customTipHandler "+ event)
        const percent = Number(event.target.value)
        billContext.setBillAmount(percent)

    }

    return(
        <div className="container-custom">      
        <input className='container-child' onChange={customTipHandler} required value= {customTip} type="number"></input>
        </div>
    )
}
