import { createContext, useState } from "react";

 export const BillContext = createContext();

 export function ContextProvider({children}){
    const [billAmount,setBillAmount] = useState(18);
    const valueToBeShared = {billAmount,setBillAmount}

    return(
        <BillContext.Provider value={valueToBeShared}>
            {children}
        </BillContext.Provider>
    )
 }
