import React from 'react';
import './App.css';
import { UserInputComponent } from './components/input/UserInputComponent.jsx';
import { ResultComponent } from './components/result/ResultComponent.jsx';
import {BillContext} from './store/bill-amount-context.jsx'

class App extends React.Component {

  render() {
    return (
   <BillContext.Provider value={{billAmount:21}}>
  <div className='flex-container'> 
     <div className='flex-item-left'>    
      <UserInputComponent></UserInputComponent>
     </div>
     <div className='flex-item-right'>
      <ResultComponent></ResultComponent>
      </div>
      </div>
      </BillContext.Provider>
    );

  }
}
export default App;
