import './TipBox.css'
import { useSelector,connect } from 'react-redux';

export const TipBox =(props)=>{
    const handler = props.onSelect;
    const currentTip = props.children
    const TipPercent = useSelector(state=>state.TipPercent)
    const isTipCurrent = currentTip==TipPercent? true : false ;


    console.log(`currentTip : ${currentTip} ,TipPercent :${TipPercent} ..... ${isTipCurrent} `)


    return(
        // <div onClick={handler} className='parent'>
        //     <div>{props.children}</div>
        // </div>
        <button onClick={handler} className={`${isTipCurrent?'parent-click':'parent'}`}>{props.children}</button>
    )
}
