import { TipBox } from '../UI/TipBox'
import { UseSelector,useDispatch } from 'react-redux'
import './SelectTipComponent.css'
import { useBillContext } from '../../store/BillContext';


export const SelectTipComponent =()=>{
    const dispatch  = useDispatch()
    const billContext = useBillContext()


    const percentDispatchHandler =(percentValue)=>{

        dispatch({type:'set-tip-percenttage',amount:{percentValue}});
        console.log("dispatcher is called" + percentValue)
    }
    return(
        <>
        <div id='container-select'>
            <TipBox onSelect ={()=>percentDispatchHandler(1)}>1</TipBox>
            <TipBox onSelect ={()=>percentDispatchHandler(2)}>2</TipBox>
            <TipBox onSelect ={()=>percentDispatchHandler(3)}>3</TipBox>
            <TipBox onSelect ={()=>percentDispatchHandler(4)}>4</TipBox>
            <TipBox onSelect ={()=>percentDispatchHandler(5)}>5</TipBox>
            <TipBox onSelect ={()=>percentDispatchHandler(6)}>6</TipBox>
        </div>
        </>
    )
    }
